// emailController.js
require('@babel/register')({
  presets: ['@babel/preset-env'],
});

const nodemailer = require('nodemailer');
const dotenv = require('dotenv');
const ReactDOMServer = require('react-dom/server');
const testingEmail  = require('../../../frontend/src/components/emailTemplates/testingEmail.jsx');

dotenv.config();

const transporter = nodemailer.createTransport({
  service: 'gmail',
  auth: {
    user: 'startwithbinno@gmail.com',
    pass: 'ksclipkoympevnpk'
  },
});


const sendEmail = async (req, res) => {
  try {
    const htmlEmail = ReactDOMServer.renderToString(testingEmail);

    const htmlSend = `
      <!DOCTYPE html>
      <html lang="en">
      <head>
        <meta charset="UTF-8">
        <meta name="viewport" content="width=device-width, initial-scale=1.0">
        <title>React Component to HTML</title>
      </head>
      <body>
        ${htmlEmail}
      </body>
      </html>
    `;

    const mailOptions = {
      from: 'startwithbinno@gmail.com', // replace with your email
      to: '1130marcusa@gmail.com', // replace with the recipient's email
      subject: 'Another Email Mothafucka!',
      html: htmlSend,
    };
    
    transporter.sendMail(mailOptions, (error, info) => {
      if (error) {
        console.error(error);
      } else {
        console.log('Email sent: ' + info.response);
      }
    });
  } catch (error) {
    console.log(error);
  }
};


module.exports = {
    sendEmail,
};

